import React from 'react';
import { Link } from 'react-router-dom';
import './Cards.css'; // Ensure this file exists for styling

const Cards = ({ detail }) => {
  console.log('Received detail:', detail); // Debugging line

  return (
    <div className='meals'>
      {
        !detail || detail.length === 0
          ? <p>Sorry, Data Not Found</p>
          : detail.map((curItem) => (
            <div className='mealImg' key={curItem.dishId}>
              <img src={curItem.image} alt={curItem.title} />
              <div className='mealContent'>
                <p className='mealTitle'>{curItem.title}</p>
                <Link to={`/dishes/${curItem.id}`}>
                  <button className='recipeButton'>Recipe</button>
                </Link>
              </div>
            </div>
          ))
      }
    </div>
  );
}

export default Cards;
