import React, { useState } from 'react';
import '../Styles/Header.css';

const Header = () => {
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    // Handle the search functionality
    console.log('Search query:', searchQuery);
  };

  const startVoiceRecognition = () => {
    const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
    recognition.lang = 'en-US';
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;

    recognition.onresult = (event) => {
      const speechToText = event.results[0][0].transcript;
      setSearchQuery(speechToText);
      // Handle the search functionality with the recognized text
      console.log('Voice search query:', speechToText);
    };

    recognition.start();
  };

  return (
    <div>
      <nav>
        <ul>
          <li>
            <img src='https://www.brandbucket.com/sites/default/files/logo_uploads/552328/large_tastysite.png' alt='Logo' />
          </li>
          <li><a href='/'>Home</a></li>
          <li><a href='/states'>Recipes</a></li>
          <li><a href='/about'>About</a></li>
          <li>
            <form onSubmit={handleSearchSubmit}>
              <input
                type="text"
                value={searchQuery}
                onChange={handleSearchChange}
                placeholder="Search..."
              />
              <button type="submit">Search</button>
            </form>
          </li>
          <li>
            <button onClick={startVoiceRecognition}>ðŸŽ¤</button>
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default Header;
