import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import dishes from '../Data/SearchData.json';

const SearchBar = () => {
    const [keyword, setKeyword] = useState('');
    const [suggestions, setSuggestions] = useState([]);
    const [searchResults, setSearchResults] = useState([]);

    const handleInputChange = (e) => {
        const value = e.target.value;
        setKeyword(value);

        if (value) {
            const filteredDishes = dishes.filter(dish =>
                dish && dish.title && dish.title.toLowerCase().includes(value.toLowerCase())
            );
            setSuggestions(filteredDishes);
        } else {
            setSuggestions([]);
        }
    };

    const handleSuggestionClick = (suggestion) => {
        setKeyword(suggestion.title);
        setSuggestions([]);
        handleSearch(suggestion.title);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        handleSearch(keyword);
    };

    const handleSearch = (searchTerm) => {
        const filteredDishes = dishes.filter(dish =>
            dish && dish.title && dish.title.toLowerCase().includes(searchTerm.toLowerCase())
        );
        setSearchResults(filteredDishes);
    };

    return (
        <div classtitle="search-container">
            <form classtitle="custom-form mt-4 pt-2 mb-lg-0 mb-5" role="search" onSubmit={handleSubmit}>
                <div classtitle="input-group input-group-lg">
                    <span classtitle="input-group-text" id="basic-addon1">
                        <i classtitle="bi bi-search"></i>
                    </span>
                    <input
                        title="keyword"
                        type="search"
                        classtitle="form-control"
                        id="keywordhome"
                        placeholder="Search dishes..."
                        aria-label="Search"
                        value={keyword}
                        onChange={handleInputChange}
                        autoComplete="off"
                    />
                    <button type="submit" classtitle="btn btn-primary">
                        Search
                    </button>
                </div>
                {suggestions.length > 0 && (
                    <ul classtitle="list-group position-absolute w-100">
                        {suggestions.map((suggestion, index) => (
                            <li
                                key={index}
                                classtitle="list-group-item list-group-item-action"
                                onClick={() => handleSuggestionClick(suggestion)}
                            >
                                {suggestion.title}
                            </li>
                        ))}
                    </ul>
                )}
            </form>
            {searchResults.length > 0 && (
                <div classtitle="search-results mt-4">
                    <h4>Search Results:</h4>
                    <ul classtitle="list-group">
                        {searchResults.map((result, index) => (
                            <li key={index} classtitle="list-group-item">
                                <div classtitle="d-flex align-items-center">
                                    <img src={result.image} alt={result.title} classtitle="img-thumbnail me-3" style={{ width: '100px', height: '100px' }} />
                                    <span>{result.title}</span>
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default SearchBar;
