import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const states = ['TELANGANA', 'TAMILNADU', 'KERALA', 'BENGAL', 'DELHI'];

function App() {
  const [isDropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => setDropdownOpen(!isDropdownOpen);

  return (
    <div>
      <nav>
        <ul>
          <li>
            <button onClick={toggleDropdown}>
              Recipes
            </button>
            {isDropdownOpen && (
              <ul className="dropdown">
                {states.map(state => (
                  <li key={state}>
                    <Link to={`/dishes/${state}`}>{state}</Link>
                  </li>
                ))}
              </ul>
            )}
          </li>
          {/* Other nav items */}
        </ul>
      </nav>
      {/* Route setup and other components */}
    </div>
  );
}

export default App;
