import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import recipes from '../Data/meals.json'; // Adjust the path based on your file structure

const Recipe = () => {
  const [data, setData] = useState(null);
  const { meal } = useParams();

  useEffect(() => {
    const fetchData = () => {
      const recipeData = recipes.find((item) => item.id === parseInt(meal, 10));
      setData(recipeData);
    };

    if (meal) {
      fetchData();
    }
  }, [meal]);

  return (
    <div>
      {!data ? (
        "Not Found"
      ) : (
        <div className='msg'>
          <div className="detail-card">
            <div className="detail-section">
              <img src={data.image} alt={data.title} />
            </div>
          </div>
          <div>
            <div className="detail-details">
              <h1 style={{ fontWeight: "bold" }}>{data.title}</h1>
              <p>{data.details}</p>
              <br /><br />

              <h3 className="instructions-header">
                INSTRUCTIONS:
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="26"
                  height="26"
                  fill="currentColor"
                  className="bi bi-bookmark-fill bookmark-icon"
                  viewBox="0 0 16 16"
                >
                  <path d="M2 2v13.5a.5.5 0 0 0 .74.439L8 13.069l5.26 2.87A.5.5 0 0 0 14 15.5V2a2 2 0 0 0-2-2H4a2 2 0 0 0-2 2" />
                </svg>
              </h3>

              <button>Step-by-Step</button><button>View All Steps</button>
              <p>{data.instructions}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Recipe;
