import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../Styles/Header.css';

const states = ['TELANGANA', 'TAMILNADU', 'KERALA', 'BENGAL', 'DELHI'];

function Header() {
    const [isDropdownOpen, setDropdownOpen] = useState(false);
    const [isMenuOpen, setMenuOpen] = useState(false);

    const toggleDropdown = () => setDropdownOpen(!isDropdownOpen);
    const toggleMenu = () => setMenuOpen(!isMenuOpen);

    return (
        <header>
            <nav className="navbar">
                <div className="navbar-brand">
                    <img src='https://www.brandbucket.com/sites/default/files/logo_uploads/552328/large_tastysite.png' alt="Logo" />
                    <button className="menu-toggle" onClick={toggleMenu}>
                        â˜°
                    </button>
                </div>
                <ul className={`nav-links ${isMenuOpen ? 'open' : ''}`}>
                    <li><Link to="/">Home</Link></li>
                    <li className="dropdown-container">
                        <a className="dropdown-toggle" onClick={toggleDropdown}>
                            Recipes
                        </a>
                        {isDropdownOpen && (
                            <ul className="dropdown">
                                {states.map((state) => (
                                    <li key={state} className="dropdown-item">
                                        <Link to={`/dishes/${state}`} onClick={toggleMenu}>{state}</Link>
                                    </li>
                                ))}
                            </ul>
                        )}
                    </li>
                    <li><Link to="/about">About</Link></li>
                </ul>
            </nav>
        </header>
    );
}

export default Header;
