    const { recipesID } = useParams();
    const course = courses.find(c => c.id === parseInt(courseId));

    return (
        <div>
      {course ? (
        <>
          <h1>-Course Details-</h1><br/>
          <div className="outer-card">
            <div className="inner-card">
              <div className="left-section">
                <img src={course.image} alt={course.title} className="card-image" />
              </div>
              <div className="right-section">
              <h1>{course.title}</h1><br/><br/>
                <p>{course.description}</p><br/><br/>
               <center><a href={course.link} className="goto">Start learning</a>
               <a href={course.syllabus} className="goto">View Syllabus</a>
               <a href={course.projects} className="goto">Complete Task</a>
               </center> 
                </div>
            </div>
            </div>
        </>
      ) : (
        <h2>Course not found</h2>
      )}
    </div>
    );
}

export default DetailRecipe;
