import React from 'react';
import { useParams } from 'react-router-dom';
import recipes from '../Data/recipes.json';

function DetailRecipe() {
    const { recipesID } = useParams();
    const  recipe =  recipes.find(c => c.id === parseInt( recipesId));

    return (
        <div>
      { recipe ? (
        <>
          <h1>-Course Details-</h1><br/>
          <div className="outer-card">
            <div className="inner-card">
              <div className="left-section">
                <img src={ recipes.image} alt={ recipes.title} className="card-image" />
              </div>
              <div className="right-section">
              <h1>{ recipes.title}</h1><br/><br/>
                <p>{ recipes.description}</p><br/><br/>
               <center><a href={ recipes.link} className="goto">Start learning</a>
              
               </center> 
                </div>
            </div>
            </div>
        </>
      ) : (
        <h2> recipes not found</h2>
      )}
    </div>
    );
}

export default DetailRecipe;
