import React from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import dishesData from '../Data/dishes.json';
import '../Styles/DishDetails.css'; // CSS file for styles

function DishDetail() {
    const { stateId, dishId } = useParams();
    const navigate = useNavigate();

    // Find the dish by id and stateId
    const dish = dishesData.find(d => d.id === parseInt(dishId, 10) && d.stateId === parseInt(stateId, 10));

    if (!dish) {
        return <p>Dish not found.</p>;
    }

    // Find the index of the current dish
    const currentIndex = dishesData.findIndex(d => d.id === parseInt(dishId, 10) && d.stateId === parseInt(stateId, 10));
    
    // Find the previous and next dishes
    const prevDish = dishesData[currentIndex - 1];
    const nextDish = dishesData[currentIndex + 1];

    return (
        <div className='dish-detail'>
            <h1>{dish.title}</h1>
            <img src={dish.image} alt={dish.title} className='dish-detail-img' />
            <p>{dish.description}</p>
            <h2>Instructions</h2>
            <p>{dish.instructions}</p>

            <div className="navigation-buttons">
                {prevDish && (
                    <Link to={`/dishes/${prevDish.stateId}/${prevDish.id}`} className="nav-button">Previous Dish</Link>
                )}
                {nextDish && (
                    <Link to={`/dishes/${nextDish.stateId}/${nextDish.id}`} className="nav-button">Next Dish</Link>
                )}
            </div>

            <Link to={`/dishes/${stateId}`} className="back-button">Back to Dishes</Link>
        </div>
    );
}

export default DishDetail;
