import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import recipes from '../Data/meals.json'; // Adjust the path based on your file structure

const Recipe = ({ detail }) => {
  const [data, setData] = useState(null);
  const { meal } = useParams();

  useEffect(() => {
    const fetchData = () => {
      const recipeData = recipes.find((item) => item.id === meal);
      setData(recipeData);
    };

    if (meal) {
      fetchData();
    }
  }, [meal]);

  return (
    <>
      {!data ? "Not Found" :  
        <div className='msg'>
          <img src={data.thumbnail} alt={data.title} />
          <div className='info'>
            <h1>Recipe Details</h1>
            <button>{data.title}</button>
            <h3>Instructions:</h3>
            <p>{data.instructions}</p>
          </div>
        </div>
      }
    </>
  );
};

export default Recipe;
