import React, { useState } from 'react';
import dishes from '../path/to/dishes.json'; // Update with the correct path
import Cards from '../Search/Cards';

const Food = () => {
  const [search, setSearch] = useState("");
  const [data, setData] = useState([]);
  const [msg, setMsg] = useState("Search and Get Recipes");

  const handleInput = (event) => {
    setSearch(event.target.value);
  };

  const myFun = () => {
    if (search === "") {
      setMsg("Please Enter Something");
    } else {
      // Assuming dishes is an object with a key "1" containing an array of meals
      const filteredData = dishes["1"].filter((meal) =>
        meal.title.toLowerCase().includes(search.toLowerCase())
      );
      setData(filteredData);
      setMsg(filteredData.length > 0 ? "Your Search Results" : "No Results Found");
    }
  };

  return (
    <>
      <h2 className='msg' style={{ textAlign: "center" }}>{msg}</h2>
      <h1 className='head'></h1>
      <div className='container'>
        <div className='searchBar'>
          <input placeholder='Search Meals' type='text' onChange={handleInput} />
          <button onClick={myFun}>Search</button>
        </div>
        <div>
          <Cards detail={data} />
        </div>
      </div>
    </>
  );
};

export default Food;
