import React from 'react';
import '../Styles/Recipes.css';
function Recipes() {
    return (
        <div>
    <section className="rcard">
      <div className="lsection">
      Explore, cook, and cherish recipes co-created by our global community of food enthusiasts.
        </div>

      <div className="rsection">
        <img src="./Images/pic2.png" alt="Developer" />
      </div>
    </section>
        
    <h1>-Courses Categories-</h1>
            <div className="courses-list">
                {recipes.map(course => (
                    <div key={course.id} className="card">
                        <img src={course.image} className="card-img-top" alt="Course Image" />
                        <div className="card-body">
                            <h5 className="card-title"><b>{course.title}</b></h5>
                        </div>
                        <Link to={`/details/${course.id}`} className="cardbutton">Go to Course</Link>
                    </div>
                ))}
            </div>




        </div>
    );
}

export default Recipes;
