import React from 'react';
import  '../Styles/States.css';

function States() {
    return (
        <div>
        <section className="Statecard" style={{backgroundColor:"rgba(248, 166, 59, 0.952)"}}>
    <div className="collectionsection">
        <p style={{fontSize:"2.5rem",fontWeight:"bold"}}>
        Explore, cook, and cherish recipes co-created by our global community of food enthusiasts.
        </p>
    </div>
       

      <div className="imagesection">
        <img src="./Images/pic2.png" alt="Developer" />
      </div>
    </section>
    
    <div>
            <h1>-States Categories-</h1>
            <div className="courses-list">
                {states.map(state => (
                    <div key={states.id} className="card">
                        <img src={states.image} className="card-img-top" alt={states.title} />
                        <div className="card-body">
                            <h5 className="card-title"><b>{states.title}</b></h5>
                        </div>
                        <Link to={`/details/${states.id}`} className="cardbutton">Go to Course</Link>
                    </div>
                ))}
            </div>
        </div>
    
    </div>
    );
}

export default States;
