// src/Components/IndiaMap.js
import React from 'react';
import '../Styles/IndiaMap.css'; // Ensure the CSS file is also correctly imported

const IndiaMap = () => {
  const handleStateClick = (stateName) => {
    console.log(`${stateName} clicked!`);
    // You can add more actions here, like routing or state updates
  };

  return (
    <div className="india-map-container">
      <svg 
        xmlns="http://www.w3.org/2000/svg" 
        viewBox="0 0 1000 1000" 
        className="india-map"
        aria-labelledby="title"
      >
        <title id="title">Map of India</title>
        <g>
          <path id="andhra-pradesh" d="M..." onClick={() => handleStateClick('Andhra Pradesh')} />
          <path id="arunachal-pradesh" d="M..." onClick={() => handleStateClick('Arunachal Pradesh')} />
          <path id="assam" d="M..." onClick={() => handleStateClick('Assam')} />
          {/* Add paths for all 29 states here */}
        </g>
      </svg>
    </div>
  );
};

export default IndiaMap;
