import { useState } from 'react';
import './App.css';
import Header from './Components/Header';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import Home from './Components/Home';
import About from './Components/About';
import Dishes from './Components/Dishes';
import Recipes from './Components/Recipes';
import DishDetails from './Components/DishDetails';
import VoiceSearch from './VoiceSearch';


function App() {
  const [voiceSearch, setVoiceSearch] = useState(false); 

  const openVoiceSearch =()=>{
    setVoiceSearch(true);
  }
  const closeVoiceSearch =()=>{
    setVoiceSearch(false);
  }
  return (
   <div>
    {
      voiceSearch?(<VoiceSearch closeVoiceSearch={closeVoiceSearch}/>):null
    }
<switch>
  <Route path='/' Component={()=> <Home setVoiceSearc={setVoiceSearch} openVoiceSearch/>}>
  </Route>
</switch>

    <BrowserRouter>
        <Header />
        <Routes>
          <Route path="/" element={<Home />}></Route>
          <Route path="/recipes" element={<Recipes />} />
          <Route path="/about" element={<About />}></Route>
          <Route path="/dishes/:stateId" element={<Dishes />}></Route>
          <Route path="/detail/:dishId"  element={<DishDetails />} />
        </Routes>
        </BrowserRouter>
   </div>
  )
}

export default App;
