import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion'; // Import motion from framer-motion
import '../Styles/Recipes.css';

const states = ['State 1', 'State 2', 'State 3'];

function StatesPage(props) {
    return (
        <div>
            <section className="rcard">
                <div className="lsection">
                    Explore, cook, and cherish recipes co-created by our global community of food enthusiasts.
                </div>
                <div className="rsection">
                    <img src="./Images/pic2.png" alt="Developer" />
                </div>
            </section>
            <div className="statescontainer">
      {statesData.map((stateData, index) => (
        <motion.div
          key={index}
          className="state"
          whileHover={{ scale: 1.1, backgroundColor: '#45a049' }}
          whileTap={{ scale: 0.9 }}
          transition={{ duration: 0.3 }}
        >
          <h1>Dishes in {stateData.state}</h1>
          <ul>
            {stateData.dishes.map(dish => (
              <li key={dish.id}>
                <Link to={`/dish/${stateData.state}/${dish.id}`}>
                  <h2>{dish.title}</h2>
                  <img src={dish.image} alt={dish.title} width="100" />
                </Link>
              </li>
            ))}
          </ul>
        </motion.div>
      ))}
    </div>
        </div>
    );
}

export default StatesPage;
