import React from 'react';
import '../Styles/Header.css';

const states = ['TELANGANA', 'TAMILNADU', 'KERALA', 'BENGAL', 'DELHI'];

  
function Header() {
    const [isDropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => setDropdownOpen(!isDropdownOpen);

    return (
        <div>
            <nav>
                <ul>
                    <li>
                     <img src='https://www.brandbucket.com/sites/default/files/logo_uploads/552328/large_tastysite.png'></img>
                    </li>
                    <li><a href='/'>Home</a></li>
                    <li>
            <button onClick={toggleDropdown}>
              Recipes
            </button>
            {isDropdownOpen && (
              <ul className="dropdown">
                {states.map(state => (
                  <li key={state}>
                    <Link to={`/dishes/${state}`}>{state}</Link>
                  </li>
                ))}
              </ul>
            )}
          </li>                    <li><a href='/about'>About</a></li>

                </ul>
            </nav>
        </div>
    );
}

export default Header;
