import React from 'react';
import { Link } from 'react-router-dom';
import state from '../Data/state.json';
import '../Styles/Recipes.css';

function Recipes() {
    return (
        <div>
    <section className="rcard">
      <div className="lsection">
      Explore, cook, and cherish recipes co-created by our global community of food enthusiasts.
        </div>

      <div className="rsection">
        <img src="./Images/pic2.png" alt="Developer" />
      </div>
    </section>
        
    <h1>-States Categories-</h1>
            <div className="recipes-list">
                {statesData.map(state => (
                    <div key={state.id} className="cards">
                        <img src={state.image} className="card-img" alt={`${state.title} Food`} />
                        <div className="card-body">
                            <h5 className="card-title" style={{textAlign:"center"}}><b>{state.title}</b></h5>
                        </div>
                        <Link to={`/dishes/${state.id}`} className="cardbutton">Go to Dishes</Link>
                    </div>
                ))}
            </div>
            </div>
    );
}

export default Recipes;
