import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import recipes from '../Data/meals.json'; // Ensure the correct path

const Recipe = () => {
  const [data, setData] = useState(null);
  const { meal } = useParams();

  useEffect(() => {
    console.log("Imported recipes:", recipes); // Debugging: Check if recipes is loaded correctly
    console.log("Meal ID:", meal); // Debugging: Check if the correct meal ID is being passed

    const fetchData = () => {
      if (Array.isArray(recipes)) {
        const recipeData = recipes.find((item) => item.id === parseInt(meal, 10));
        setData(recipeData);
      } else {
        console.error("Error: recipes is not an array or is undefined.");
      }
    };

    if (meal) {
      fetchData();
    }
  }, [meal]);

  return (
    <>
      {!data ? (
        "Not Found"
      ) : (
        <div className='msg'>
          <img src={data.image} alt={data.title} />
          <div className='info'>
            <h1>{data.title}</h1>
            <p>{data.details}</p>
            <h3>Instructions:</h3>
            <p>{data.instructions}</p>
          </div>
        </div>
      )}
    </>
  );
};

export default Recipe;
