import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import dishesData from '../Data/dishes.json';
import './Details.css'; // Import your CSS file

const Details = () => {
  const { stateId, dishId } = useParams();
  const dish = dishesData[stateId]?.find(d => d.id === parseInt(dishId, 10));
  const [currentStep, setCurrentStep] = useState(0);
  const [viewAll, setViewAll] = useState(false);

  if (!dish) {
    return <p>Dish not found.</p>;
  }

  const handleNext = () => {
    setCurrentStep((prevStep) => (prevStep + 1) % dish.instructions.length);
  };

  const handlePrevious = () => {
    setCurrentStep((prevStep) => (prevStep - 1 + dish.instructions.length) % dish.instructions.length);
  };

  const toggleViewAll = () => {
    setViewAll(!viewAll);
  };

  return (
    <div className="dish-details">
      <div className="image-container">
        <img src={dish.image} alt={dish.title} />
      </div>
      <h1>{dish.title}</h1>
      <div className="instructions-container">
        {viewAll ? (
          <div className="all-steps">
            <h2>All Steps</h2>
            <ol>
              {dish.instructions.map((step, index) => (
                <li key={index}>{step}</li>
              ))}
            </ol>
            <button onClick={toggleViewAll}>View Step-by-Step</button>
          </div>
        ) : (
          <div className="step-by-step">
            <h2>Step {currentStep + 1} of {dish.instructions.length}</h2>
            <p>{dish.instructions[currentStep]}</p>
            <div className="navigation-buttons">
              <button onClick={handlePrevious} disabled={currentStep === 0}>Previous</button>
              <button onClick={handleNext} disabled={currentStep === dish.instructions.length - 1}>Next</button>
            </div>
            <button onClick={toggleViewAll}>View All Steps</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Details;
