import React from 'react';
import { useParams } from 'react-router-dom';
import dishesData from '../Data/dishes.json';
import '../Styles/DishDetails.css'; // CSS file for styles

function DishDetail() {
    const { stateId, dishId } = useParams(); // Extract stateId and dishId from URL parameters

    // Find the dish by stateId and dishId
    const dish = dishesData.find(d => d.stateId === parseInt(stateId, 10) && d.id === parseInt(dishId, 10));

    if (!dish) {
        return <p>Dish not found.</p>;
    }

    return (
        <div className='dish-detail'>
            <h1>{dish.title}</h1>
            <img src={dish.image} alt={dish.title} className='dish-detail-img' />
            <p>{dish.description}</p>
            <h2>Instructions</h2>
            <p>{dish.instructions}</p>
        </div>
    );
}

export default DishDetail;
