import React from 'react';
import { useParams } from 'react-router-dom';
import recipes from '../Data/recipes.json';

function DetailRecipe() {

    return (
        <div>
         <h1>-Recipes Categories-</h1>
            <div className="recipes-list">
                {recipes.map(recipe => (
                    <div key={recipe.id} className="cards">
                        <img src={recipe.image} className="card-img" alt={recipe.title} />
                        <div className="card-body">
                            <h5 className="card-title" style={{ textAlign: "center" }}><b>{recipe.title}</b></h5>
                        </div>
                        <Link to={`/detail/${recipe.id}`} className="cardbutton">Go to Recipe</Link>
                    </div>
                ))}
            </div>
            <div style={{ textAlign: 'center', marginTop: '20px' }}>
                <Link to="/more-recipes" className="cardbutton">More Recipes</Link>
            </div>
    
    </div>
    );
}

export default DetailRecipe;
