import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import statesData from '../data/statesData.json'; // Adjust the path according to your project structure

const StatesPage = () => {
  return (
    <div className="statescontainer">
      {statesData && statesData.map((stateData, index) => (
        <motion.div
          key={index}
          className="state"
          whileHover={{ scale: 1.1, backgroundColor: '#45a049' }}
          whileTap={{ scale: 0.9 }}
          transition={{ duration: 0.3 }}
        >
          <h1>Dishes in {stateData.state}</h1>
          <ul>
            {stateData.dishes.map(dish => (
              <li key={dish.id}>
                <Link to={`/dish/${stateData.state}/${dish.id}`}>
                  <h2>{dish.title}</h2>
                  <img src={dish.image} alt={dish.title} width="100" />
                </Link>
              </li>
            ))}
          </ul>
        </motion.div>
      ))}
    </div>
  );
};

export default StatesPage;
