// src/Components/IndiaMap.js
import React from 'react';
import '../Styles/IndiaMap.css'; // Include your CSS file for styling

const IndiaMap = () => {
  const handleStateClick = (stateName) => {
    console.log(`${stateName} clicked!`);
    // You can add more actions here, like routing or state updates
  };

  return (
    <div className="india-map-container">
      <img src="/Images/india-map.png" useMap="#india-map" alt="Map of India" className="india-map-image" />
      <map name="india-map">
        <area shape="poly" coords="..." alt="Andhra Pradesh" onClick={() => handleStateClick('Andhra Pradesh')} />
        <area shape="poly" coords="..." alt="Arunachal Pradesh" onClick={() => handleStateClick('Arunachal Pradesh')} />
        <area shape="poly" coords="..." alt="Assam" onClick={() => handleStateClick('Assam')} />
        {/* Add area tags for all 29 states with appropriate coords */}
      </map>
    </div>
  );
};

export default IndiaMap;
