import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Ensure Bootstrap is included
import { useNavigate } from 'react-router-dom'; // Import useNavigate from React Router
import dishes from '../Data/SearchData.json';

const SearchForm = () => {
    const [keyword, setKeyword] = useState('');
    const [suggestions, setSuggestions] = useState([]);
    const navigate = useNavigate(); // Hook to programmatically navigate

    const handleInputChange = (e) => {
        const value = e.target.value;
        setKeyword(value);

        if (value) {
            const filteredDishes = dishes.filter(dish =>
                dish.toLowerCase().includes(value.toLowerCase())
            );
            setSuggestions(filteredDishes);
        } else {
            setSuggestions([]);
        }
    };

    const handleSuggestionClick = (suggestion) => {
        setKeyword(suggestion);
        setSuggestions([]);
        navigate(`/recipes/${encodeURIComponent(suggestion)}`); // Navigate to the recipe page
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Search term:', keyword);
        // Implement your search logic here
    };

    return (
        <div className="search-container position-relative">
            <form className="custom-form mt-4 pt-2 mb-lg-0 mb-5" role="search" onSubmit={handleSubmit}>
                <div className="input-group input-group-lg">
                    <span className="input-group-text" id="basic-addon1">
                        <i className="bi bi-search"></i>
                    </span>
                    <input
                        name="keyword"
                        type="search"
                        className="form-control"
                        id="keywordhome"
                        placeholder="Search dishes..."
                        aria-label="Search"
                        value={keyword}
                        onChange={handleInputChange}
                        autoComplete="off"
                    />
                    <button type="submit" className="btn btn-primary">
                        Search
                    </button>
                </div>
                {suggestions.length > 0 && (
                    <ul className="list-group position-absolute w-100">
                        {suggestions.map((suggestion, index) => (
                            <li
                                key={index}
                                className="list-group-item list-group-item-action"
                                onClick={() => handleSuggestionClick(suggestion)}
                            >
                                {suggestion}
                            </li>
                        ))}
                    </ul>
                )}
            </form>
        </div>
    );
};

export default SearchForm;
