import { Link,useParams } from 'react-router-dom';
import stateDishes from '../Data/dishes.json';

function Dishes() {
    const { stateId } = useParams();

    // Filter dishes by stateId
    const stateDishes = dishesData.filter(dish => dish.stateId === parseInt(stateId, 10));

    return (
        <div>
            <h1>-Dishes Categories-</h1>
            <div className="recipes-list">
                {stateDishes.map(dish => (
                    <div key={dish.id} className="cards" style={{padding:"10px", width:"400px", marginLeft:"25px"}}>
                        <img src={dish.image} className="card-img" alt={dish.title} />
                        <div className="card-body">
                            <h5 className="card-title" style={{textAlign:"center"}}><b>{dish.title}</b></h5>
                            <p style={{paddingLeft:"20px"}}>{dish.description}</p>
                        </div>
                        <Link to={`/detail/${dish.id}`} className="cardbutton">Go to Recipe</Link>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Dishes;
