import React from 'react';
import { Link, useParams } from 'react-router-dom';
import dishesData from '../Data/dishes.json';
import '../Styles/DishDetails.css'; // CSS file for styles

function Dishes() {
    const { stateId } = useParams();

    // Filter dishes by stateId
    const stateDishes = dishesData.filter(dish => dish.stateId === parseInt(stateId, 10));

    return (
        <div className='dish'>
            <h1>-Dishes Categories-</h1>
            <div className="recipes-list">
                {stateDishes.length > 0 ? (
                    stateDishes.map(dish => (
                        <div key={dish.id} className="card">
                            <img src={dish.image} className="card-img" alt={dish.title} />
                            <div className="card-body">
                                <h5 className="card-title">{dish.title}</h5>
                                <p className="card-description">{dish.description}</p>
                            </div>
                            <Link to={`/detail/${stateId}/${dish.id}`} className="card-button">Go to Recipe</Link>
                        </div>
                    ))
                ) : (
                    <p>No dishes found for this state.</p>
                )}
            </div>
        </div>
    );
}

export default Dishes;
