import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import statesData from '../Data/state.json'; // Adjust the path according to your project structure

const StatesPage = () => {
  return (
    <div>
         <section className="rcard">
                <div className="lsection">
                    Explore, cook, and cherish recipes co-created by our global community of food enthusiasts.
                </div>
                <div className="rsection">
                    <img src="./Images/pic2.png" alt="Developer" />
                </div>
            </section>
    <div className="statescontainer">
      {statesData && statesData.map((stateData, index) => (
        <motion.div
          key={index}
          className="state"
          whileHover={{ scale: 1.1, backgroundColor: '#45a049' }}
          whileTap={{ scale: 0.9 }}
          transition={{ duration: 0.3 }}>
             <li key={statesData.id}>
            <Link to={`/dish//${statesData.id}`}>
              <h2>{statesData.title}</h2>
              <img src={statesData.image} alt={statesData.title} width="100" />
            </Link>
          </li>
        </motion.div>
        
      ))}
    </div>
    </div>

  );
};

export default StatesPage;
